INSERT INTO videogames (id, name, description, image, publication_date) VALUES (1, 'Elden Ring', 'Un vasto RPG open-world creato da FromSoftware in collaborazione con George R. R. Martin. Esplora le Terre di Mezzo, affronta boss colossali e svela una mitologia oscura e profonda in un mondo brutalmente affascinante.', 'eldenring.jpg', '2022-02-25'), (2, 'God of War', 'Un''avventura epica che mescola mitologia norrena e un racconto toccante tra padre e figlio. Kratos, il Dio della Guerra, e suo figlio Atreus affrontano divinità, mostri e i fantasmi del passato in un viaggio intenso e cinematografico.', 'gow.jpg', '2018-04-20'), (3, 'The Witcher 3', 'Vesti i panni di Geralt di Rivia, un cacciatore di mostri in un mondo fantasy vibrante e moralmente complesso. Una storia ricca, personaggi memorabili e scelte che cambiano il destino del mondo in uno degli RPG più acclamati di sempre.', 'witcher3.jpg', '2015-05-19'), (4, 'Hollow Knight', 'Esplora il regno sotterraneo di Hallownest in un metroidvania mozzafiato, noto per il suo design artistico, colonna sonora malinconica e combattimenti impegnativi. Un’esperienza profonda di esplorazione e mistero.', 'hollowknight.jpg', '2017-02-24'), (5, 'Stardew Valley', 'Un’affascinante simulazione di vita rurale dove puoi coltivare, pescare, stringere amicizie, costruire una fattoria da sogno e persino sposarti. Un gioco rilassante ma ricco di contenuti e segreti.', 'stardew.jpg', '2016-02-26'), (6, 'Cyberpunk 2077', 'Un RPG futuristico ambientato in Night City, una metropoli spietata e ipertecnologica. Interpreta V, un mercenario in cerca di immortalità, in una storia che fonde cyberpunk, caos e dilemmi morali.', 'cyberpunk.jpg', '2020-12-10'), (7, 'Minecraft', 'Un fenomeno globale che permette di costruire, esplorare e sopravvivere in un mondo a blocchi senza limiti. Sviluppa la tua creatività da solo o con amici in infinite modalità di gioco.', 'minecraft.jpg', '2011-11-18'), (8, 'Celeste', 'Un platform intenso e toccante che racconta la scalata interiore e reale della protagonista Madeline. Supera ostacoli durissimi in un viaggio sulla montagna simbolo delle proprie paure e speranze.', 'celeste.jpg', '2018-01-25'), (9, 'Overwatch', 'Uno sparatutto multiplayer a squadre con eroi unici, abilità spettacolari e dinamiche di gioco cooperative. Lotta per la vittoria in arene colorate e strategiche con uno stile grafico accattivante.', 'overwatch.jpg', '2016-05-24'), (10, 'Red Dead Redemption 2', 'Un''epopea western firmata Rockstar Games che ti mette nei panni di Arthur Morgan, fuorilegge in cerca di redenzione. Un open world vivo, realistico e ricco di emozioni, azione e narrativa.', 'rdr2.jpg', '2018-10-26');
INSERT INTO platforms (id, name) VALUES (1, 'PC'), (2, 'PlayStation 5'), (3, 'Xbox Series X'), (4, 'Nintendo Switch'), (5, 'Steam Deck');
